import React from "react";
import Layout from "../components/Layout";
import axios from "axios";
axios.defaults.withCredentials = true;

axios.interceptors.request.use(
  (config) => {
    const { origin } = new URL(config.url);
    const allowedOrigins = [apiUrl];
    const token = localStorage.getItem("token");
    if (allowedOrigins.includes(origin)) {
      config.headers.authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

const cart = ({ data }) => {
  console.log("ywessssssssssssssssssssssssssssssssssss", data);

  return (
    <Layout>
      <section className="cart">
        <div className="cart__description">
          <i className="fas fa-shopping-cart"></i>
          <p className="cart__empty">your cart is empty</p>
        </div>
        <div className="cart__details">
          <p className="cart__phrase">
            have an account ?
            <a className="cart__login" href="/login">
              login
            </a>
            to view items in your cart
          </p>
        </div>

        <div className="">
          <p>{data.user.name}</p>
        </div>

        <p className="cart__shopping">start shopping</p>

        <div className="cart__help">
          <p className="cart__chat">chat representative</p>
          <p className="cart__call">need help ? call us</p>
        </div>
      </section>
    </Layout>
  );
};

const b = axios.create({
  baseURL: "http://localhost:3000",
  withCredentials: true,
});

export async function getServerSideProps() {
  const { data } = await b.get("http://localhost:3000/api/users/cart");

  return {
    props: {
      data,
    },
  };
}

export default cart;
