import React from "react";
import Layout from "../components/Layout";
import axios from "axios";

// axios.interceptors.request.use(
//   (config) => {
//     const token =
//       "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjVmOWUzYmJhMTQ3NWFmNGYzMDMxYzg4MCIsImlhdCI6MTYwNDMwMjM5OCwiZXhwIjoxNjEyMDc4Mzk4fQ.3V5Xqt0Ms7_OsRmwhhgZASmWWTEyt1iLUeWiDGLeY5M";

//     console.log("token1111", token);

//     config.headers.authorization = `Bearer ${token}`;

//     return config;
//   },
//   (error) => {
//     return Promise.reject(error);
//   }
// );

class cart extends React.Component {
  async componentDidCatch() {
    const token = localStorage.getItem("token");
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    const { data } = await axios.get("http://localhost:3000/api/users/cart", config);
  }

  render() {
    return (
      <Layout>
        <section className="cart">
          <div className="cart__description">
            <i className="fas fa-shopping-cart"></i>
            <p className="cart__empty">your cart is empty</p>
          </div>
          <div className="cart__details">
            <p className="cart__phrase">
              have an account ?
              <a className="cart__login" href="/login">
                login
              </a>
              to view items in your cart
            </p>
          </div>

          <div className="">
            <p>{}</p>
          </div>

          <p className="cart__shopping">start shopping</p>

          <div className="cart__help">
            <p className="cart__chat">chat representative</p>
            <p className="cart__call">need help ? call us</p>
          </div>
        </section>
      </Layout>
    );
  }
}

export async function getServerSideProps() {
  const { data } = await axios.get("http://localhost:3000/api/users/cart");

  return {
    props: {
      data,
    },
  };
}

export default cart;
