import React from "react";
import Link from "next/link";
import axios from "axios";

import product from "../data/products.json";

const Product = () => {
  return (
    <section className="product">
      <h4 className="product__phrase">trending this week</h4>
      <ul className="product__list">
        {product.map((product) => {
          return (
            <li className="product__item">
              <Link
                as={`/productpage?${product.slug}-${product.id}`}
                href={`/productpage?slug=${product.slug}-${product.id}`}
              >
                <a className="product__link">
                  <img src={product.image} alt="" className="product__img" />
                  <div className="product__details">
                    <p className="product__name">{product.name}</p>
                    <p className="product__price">{product.price}</p>
                  </div>
                </a>
              </Link>

              <div className="product__addtocart">
                <p className="product__addtocart--text">add to cart</p>
              </div>
            </li>
          );
        })}
      </ul>
    </section>
  );
};

export default Product;
