import React from "react";
import Layout from "../components/Layout";
import style from "../pages/index.module.scss";
import { loginUser } from "../server/controllers/fauthContoller";

class login extends React.Component {
  state = {
    email: "",
    password: "",
    data: {},
  };

  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleSubmit = (event) => {
    event.preventDefault();

    const { email, password } = this.state;
    loginUser.then((data) => {
      this.setState({ data });
    });
    console.log(this.state);
  };

  render() {
    return (
      <Layout>
        <section className="siginin" id="signin">
          <a href="/" className="siginin__sitename" id="sitename">
            iguyra <span>&larr;</span>
          </a>
          <form className="siginin__form" onSubmit={this.handleSubmit} action="">
            <label className="siginin__label" for="email">
              email
              <input
                className="siginin__input"
                name="email"
                type="email"
                id="email"
                placeholder="eg: res@res.com"
                onChange={this.handleChange}
              />
            </label>
            <label className="siginin__label" for="password">
              password
              <input
                className="siginin__input"
                name="password"
                type="password"
                id="password"
                placeholder="type password"
                onChange={this.handleChange}
              />
            </label>

            <button className="siginin__button">login</button>

            <div className="siginin__details">
              <a className="siginin__passwordforgot" href="#">
                forgot your password ?
              </a>
              <a className="siginin__register" href="/signup" id="createaccount">
                create an account
              </a>
            </div>
          </form>
        </section>
      </Layout>
    );
  }
}

export default login;
