import { useContext } from "react";
import Layout from "../components/Layout";
import axios from "axios";
import { withRouter } from "next/router";
import { AppContext } from "../context/appContext";

const productpage = (props) => {
  const { product } = props;
  const [cart, setCart] = useContext(AppContext);

  console.log("CART", cart);

  return (
    <Layout>
      <main>
        <section class="productpage">
          <div class="productpage__imgcontainer" id="imgcontainer">
            <img src={product.image} alt="" class="productpage__img" />

            <img src="/static/llili2.jpg" alt="" class="productpage__img" />
            <img src="/static/llili2.jpg" alt="" class="productpage__img" />
          </div>

          <div class="productpage__productimgzoom" id="productimgzoom">
            <div class="productpage__zoomimg">
              <img src="/static/llili2.jpg" alt="" class="productpage__zoom" />
              <img src="/static/llili2.jpg" alt="" class="productpage__zoom" />
              <img src="/static/llili2.jpg" alt="" class="productpage__zoom" />
            </div>
            <div class="close" id="close">
              x
            </div>
          </div>

          <div class="productpage__detail">
            <p class="productpage__name">{product.name}</p>
            <p class="productpage__price">GH {product.price}</p>
          </div>

          <div class="productpage__descriptionbox">
            <p class="productpage__title">description</p>
            <p class="productpage__description">{product.description}</p>
          </div>
        </section>

        <section class="addtocart">
          <div class="addtocart__container">
            <div class="addtocart__call">
              <a href="#" class="addtocart__calllink">
                <i class="fas fa-phone-alt"></i>
              </a>
            </div>
            <div class="addtocart__add">
              <a href="#" class="addtocart__addlink">
                add to cart
              </a>
            </div>
          </div>
        </section>

        <section class="return">
          <div class="return__container">
            <p class="return__icon">
              <i class="fas fa-truck"></i>
            </p>
            <p class="return__phrase">delievers within 3 days</p>
          </div>

          <div class="return__container">
            <p class="return__icon">
              <i class="fas fa-undo-alt"></i>
            </p>
            <p class="return__phrase">free return within 6 days</p>
          </div>
        </section>
      </main>
    </Layout>
  );
};

productpage.getInitialProps = async function (context) {
  // let {
  //   query: { slug },
  // } = context;
  // let id = slug ? parseInt(slug.split("-")[1]) : context.query.id;

  const slug = context.query.slug;
  const id = slug.split("-")[1];

  const { data } = await axios.get("http://localhost:3000/api/products");

  const products = data.product;
  const product = products.find((el) => {
    return el._id === id;
  });

  console.log("product", product, id);

  return {
    product,
  };
};

export default withRouter(productpage);
