import React from "react";
import Layout from "../components/Layout";

export default class Slides extends React.Component {
  state = {
    email: "",
    password: "",
    data: {},
  };

  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleSubmit = async (event) => {
    event.preventDefault();

    const { email, password } = this.state;
    console.log("email", email, password);
    console.log("on signup");
    const { data } = await axios.post("http://localhost:3000/api/users/signup", { email, password });

    console.log("signup user", data);

    this.setState({ data: data });
  };

  render() {
    return (
      <Layout>
        <section class="signup" id="signup">
          <a href="/index.html" class="signup__sitename" id="sitename1">
            iguyra <span>&larr;</span>
          </a>
          <form class="signup__form" action="">
            <label class="signup__label" htmlFor="email">
              email
              <input
                class="signup__input"
                type="email"
                id="email1"
                placeholder="eg: res@res.com"
                onChange={this.handleChange}
              />
            </label>
            <label class="signup__label" htmlFor="password">
              password
              <input
                class="signup__input"
                type="password"
                id="password1"
                placeholder="type password"
                onChange={this.handleChange}
              />
            </label>
            <label class="signup__label" htmlFor="password">
              confirm password
              <input
                class="signup__input"
                type="password"
                id="confirmpassword"
                placeholder="type password"
                onChange={this.handleChange}
              />
            </label>

            <button class="signup__button">create account</button>

            <div class="signup__details">
              <a class="signup__register" href="/login.html" id="backlogin">
                login
              </a>
            </div>
          </form>
        </section>
      </Layout>
    );
  }
}
