import React, { useEffect, useState } from "react";

const CartWithItem = (props) => {
  const [products, setProducts] = useState([]);
  useEffect(() => {
    const products = JSON.parse(localStorage.getItem("isavecart"));
    setProducts(products);
    console.log("productsvin", products);
  }, []);

  console.log("products", products.products);

  return (
    <section class="cartitem">
      <div class="cartitem__messagedisplay">
        <p class="cartitem__update">your cart is updated</p>
        <p class="cartitem__itemnumber">my cart (2 items)</p>
      </div>
      {products.products
        ? products.products.map((product) => {
            return (
              <React.Fragment>
                <div key={product.productId} class="cartitem__top">
                  <img src={product.image} alt="" class="cartitem__img" />
                  <div class="cartitem__details">
                    <p class="cartitem__productname">{product.name}</p>
                    <p class="cartitem__size">size: one size fits all</p>
                    <p class="cartitem__price">GH {product.price}</p>
                  </div>
                </div>
                <div class="cartitem__bottom">
                  <div class="cartitem__control">
                    <i class="far fa-heart"></i>
                    <i class="fas fa-trash-alt">remove</i>
                  </div>
                  <div class="cartitem__incdes">
                    <p class="cartitem__qunatity cartitem__qunatity--dec">-</p>
                    <p class="cartitem__qunatity cartitem__qunatity--number">{product.qty}</p>
                    <p class="cartitem__qunatity cartitem__qunatity--inc">+</p>
                  </div>
                </div>
              </React.Fragment>
            );
          })
        : ""}

      <div class="cartitem__checkoutcontainer">
        <div class="cartitem__container">
          <div class="cartitem__totalcontainer">
            <p class="cartitem__totalword">total</p>
            <p class="cartitem__totalprice">GH {products.totalProductsPrice}</p>
          </div>
          <p class="cartitem__policy">delievry fee not included</p>
        </div>
        <p class="cartitem__checkout" id="checkout">
          checkout
        </p>
        <p class="cartitem__call">call to order</p>
      </div>
    </section>
  );
};

export default CartWithItem;
