import React from "react";
import Link from "next/link";
import axios from "axios";

class Heading extends React.Component {
  state = {
    user: {},
  };

  async componentDidMount() {
    const token = localStorage.getItem("token");

    console.log("componentmounted", token);
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    const { data } = await axios.get("http://localhost:3000/api/users/cart", config);
    console.log("heading dataaaaaaaaaaaaaaaaaaaaaa", data);
    this.setState({ user: data.user });
  }

  render() {
    const { user } = this.state;
    console.log("render data", user);
    return (
      <section className="header">
        <div className="header__details">
          <div className="header__menu"></div>
          <Link href="/">
            <a className="header__name">iguyra</a>
          </Link>
        </div>
        <div className=""></div>
        <div className="searchtest">
          <div className="searchtest__inputcontainer">
            <input type="text" className="searchtest__input" placeholder="search product" id="searchinput" />
            <a href="#" className="searchtest__searchbutton" id="searchbutton">
              <i className="fas fa-search"></i>
            </a>
          </div>
        </div>

        <div className="header__profileDetails">
          <div className="header__profile">
            <Link href="/login">
              <a>
                <i className="fas fa-user" id="userLogin"></i>
              </a>
            </Link>
          </div>
          <div className="header__cart">
            <Link href="/cart">
              <a>
                <i className="fas fa-cart-plus"></i>
              </a>
            </Link>
          </div>
        </div>
      </section>
    );
  }
}

export default Heading;
