import { create } from "./server/models/userModel";

export const addFirstProduct = (product) => {
  let productPrice = product.price;

  //create an empty array and push the item

  let newCart = {
    products: [],
    totalProductsCount: 1,
    totalProductsPrice: +productPrice,
  };

  const newProduct = createNewProduct(product, productPrice, 1);

  newCart.products.push(newProduct);

  localStorage.setItem("isavecart", JSON.stringify(newCart));
  return newCart;
};

// create a new product object
export const createNewProduct = (product, productPrice, qty) => {
  return {
    productId: product._id,
    image: product.image,
    price: +productPrice,
    name: product.name,
    qty: qty,
    totalPrice: +productPrice * qty,
  };
};

export const updateCart = (existingCart, product, qtytoBeAdded, newQty = false) => {
  const updatedProducts = getUpdatedProducts(existingCart.products, product, qtytoBeAdded, newQty);
  console.log("updatedProducts", updatedProducts);
  console.log("updatedProducts", updatedProducts);

  const addPrice = (total, item) => {
    total.totalPrice += item.totalPrice;
    total.qty += item.qty;

    return total;
  };

  //loop through the updated proudct array and add the totALprice of each item to get the totalPrice
  let total = updatedProducts.reduce(addPrice, { totalPrice: 0, qty: 0 });

  const updatedCart = {
    products: updatedProducts,
    totalProductsCount: +total.qty,
    totalProductsPrice: +total.totalPrice,
  };
  localStorage.setItem("isavecart", JSON.stringify(updatedCart));
  return updatedCart;
};

//get updated products array
//update the product if its exist
//and add the new product to the existing cart
export const getUpdatedProducts = (existingProductInCart, product, qtyToBeAdded, newQty = false) => {
  const productExistIndex = isProductInCart(existingProductInCart, products.productId);
  console.log("product._id", product.productId);

  //if product exist (index of that product is found in te array) update the product qunatity and totalPrice
  if (-1 < productExistIndex) {
    let updatedProducts = existingProductInCart;
    let updatedProduct = updatedProducts[productExistIndex];

    //if we have the new quantity of the product avail, se that else add the qtyToBeAdded

    updatedProduct.qty = newQty ? newQty : updatedProduct.qty + qtyToBeAdded;
    updatedProduct.totalPrice = updatedProduct.price * updatedProduct.qty;
    // updatedProduct.totalPrice = updatedProduct.price * updatedProduct.qty;

    return updatedProducts;
  } else {
    //if the product is not found push the new product to the existing product array
    let productPrice = product.price;
    const newProduct = createNewProduct(product, productPrice, qtyToBeAdded);
    existingProductInCart.push(newProduct);
    return existingProductInCart;
  }
};

//return index of the product if it exists
export const isProductInCart = (existingProductInCart, productId) => {
  const returnItemThatExists = (item, index) => {
    if (productId === item.productId) {
      return item;
    }
  };

  const newArray = existingProductInCart.filter(returnItemThatExists);
  return existingProductInCart.indexOf(newArray[0]);
};

export const updateQuantity = (product, productCount) => {
  const updatedQty = +productCount + 1;
  console.log(updatedQty);
  const productPrice = product.price;

  let newCart = {
    products: [],
    totalProductsCount: updatedQty,
    totalProductsPrice: +productPrice * updatedQty,
  };
  const newProduct = createNewProduct(product, productPrice, updatedQty);

  newCart.products.push(newProduct);
  localStorage.setItem("isavecart", JSON.stringify(newCart));

  return { updatedQty, newCart };
};
