import React from "react";
import Link from "next/link";
import axios from "axios";

import products from "../data/products.json";

export default class Product extends React.Component {
  state = {
    product: [],
  };

  async componentDidMount() {
    const { data } = await axios.get("http://localhost:3000/api/products");

    this.setState({ product: data.product });
    console.log(this.state);
  }

  render() {
    console.log("this.state", this.state);
    const product = this.state.product;
    console.log("product", product[0]);

    return (
      <section class="product">
        <h4 className="product__phrase">trending this week</h4>
        <ul className="product__list">
          {products.map((product) => {
            return (
              <li className="product__item">
                <Link
                  as={`/productpage?${product.slug}-${product.id}`}
                  href={`/productpage?slug=${product.slug}-${product.id}`}
                >
                  <a className="product__link">
                    <img src={product.image} alt="" className="product__img" />
                    <div className="product__details">
                      <p className="product__name">{product.name}</p>
                      <p className="product__price">{product.price}</p>
                    </div>
                  </a>
                </Link>

                <div className="product__addtocart">
                  <p className="product__addtocart--text">add to cart</p>
                </div>
              </li>
            );
          })}
        </ul>
      </section>
    );
  }
}
