import Link from "next/link";
import React, { useContext, useState } from "react";
import { appContext } from "../../context/appContext";
import addFirstProduct from "../../functions";

const AddToCartButton = (props) => {
  const { product } = props;

  const [cart, setCart] = useContext(appContext);

  const handleAddToCartClick = () => {
    if (process.browser) {
      let existingCart = localStorage.getItem("isavecart");
    }

    //if cart the item(s) already update the existing
    if (existingCart) {
    } else {
      //if no item in the cart , create an empty array and push the item

      const newCart = addFirstProduct(product);
      setCart(newCart);
    }
  };

  return (
    <React.Fragment>
      <button onClick={handleAddToCartClick} className="addtocartbutton">
        add to cart
      </button>
    </React.Fragment>
  );
};

export default AddToCartButton;
