import { useContext } from "react";
import React from "react";
import styles from "./CartIcon.module.scss";

import Link from "next/link";
import { AppContext } from "../../context/appContext";

const CartIcon = () => {
  const [cart, setCart] = useContext(AppContext);

  const productCount = null !== cart && Object.keys(cart).length ? cart.totalProductsCount : "";

  const totalPrice = null !== cart && Object.keys(cart).length ? cart.totalProductsPrice : "";

  return (
    <React.Fragment>
      <a className={styles.totalPrice}>{totalPrice ? <span>${totalPrice}</span> : ""}</a>
      <i className="fa fa-shopping-cart cartIcon__cart"></i>
      <span>{productCount ? <span>{productCount} </span> : ""}</span>
    </React.Fragment>
  );
};
export default CartIcon;
