import React, { useEffect, useState } from "react";

const CartWithItem = (props) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const products = JSON.parse(localStorage.getItem("isavecart"));
    setProducts(products);
    console.log("productsvin", products);
  }, []);

  console.log("products", products.products);

  return (
    <section className="cartitem">
      <div className="cartitem__messagedisplay">
        <p className="cartitem__update">your cart is updated</p>
        <p className="cartitem__itemnumber">my cart (2 items)</p>
      </div>
      {products.products &&
        products.products.map((product) => {
          return (
            <React.Fragment>
              <div key={product.productId} className="cartitem__top">
                <img src={product.image} alt="" className="cartitem__img" />
                <div className="cartitem__details">
                  <p className="cartitem__productname">{product.name}</p>
                  <p className="cartitem__size">size: one size fits all</p>
                  <p className="cartitem__price">GH {product.price}</p>
                </div>
              </div>
              <div className="cartitem__bottom">
                <div className="cartitem__control">
                  <i className="far fa-heart"></i>
                  <i className="fas fa-trash-alt">remove</i>
                </div>
                <div className="cartitem__incdes">
                  <p className="cartitem__qunatity cartitem__qunatity--dec">-</p>
                  <p className="cartitem__qunatity cartitem__qunatity--number">{product.qty}</p>
                  <p className="cartitem__qunatity cartitem__qunatity--inc">+</p>
                </div>
              </div>
            </React.Fragment>
          );
        })}

      <div className="cartitem__checkoutcontainer">
        <div className="cartitem__container">
          <div className="cartitem__totalcontainer">
            <p className="cartitem__totalword">total</p>
            <p className="cartitem__totalprice">GH {products.totalProductsPrice}</p>
          </div>
          <p className="cartitem__policy">delievry fee not included</p>
        </div>
        <p className="cartitem__checkout" id="checkout">
          checkout
        </p>
        <p className="cartitem__call">call to order</p>
      </div>
    </section>
  );
};

export default CartWithItem;
